sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (isLoading || newValue === '') {
        return;
    }

    var whatneeded = g_form.getValue('u_what_needed');
    g_form.clearOptions('u_what_needed');
    if (newValue == 'fullbody') {
        g_form.addOption('u_what_needed', 'fullbody', 'fullbody 1');
        g_form.addOption('u_what_needed', 'fullbody', 'fullbody 2');
        g_form.addOption('u_what_needed', 'fullbody', 'Other');
    }
    // If the value of the Request type field is facilities, add
    // two facilities choices and other to the What needed field
    // choice list
    if (newValue == 'partial') {
        g_form.addOption('u_what_needed', 'partial', 'partial 1');
        g_form.addOption('u_what_needed', 'partial', 'partial 2');
        g_form.addOption('u_what_needed', 'partial', 'Other');
    }
    // If the value of the Request type field is legal, add
    // two legal choices and other to the What needed field
    // choice list
    if (newValue == 'internal') {
        g_form.addOption('u_what_needed', 'internal', 'internal 1');
        g_form.addOption('u_what_needed', 'internal', 'internal 2');
        g_form.addOption('u_what_needed', 'internal', 'Other');
    }

    // If the form is loading and it is not a new record, set the u_what_needed value to the
    // value from the record before it was loaded
    if (isLoading && !g_form.isNewRecord()) {
        g_form.setValue('u_what_needed', whatneeded);
    }
    //Type appropriate comment here, and begin script below

}
