sys_script_include.script:
var validateCountryUtils = Class.create();
validateCountryUtils.prototype = {
    initialize: function() {
    },

    getCountry: function() {
		var userRecord = new GlideRecord("sys_user");
		userRecord.get(this.getParameter('country'));
		return userRecord.country + '';
    },

    isValidCountry: function(){
        var country = this.getCountry();
        if(country === "India"){
            return true;
        } else {
            return false;
        }
    },

    type: 'validateCountryUtils'
};
